// @Author   : Ranshi
// @File     : main.go
func longestPalindrome(s string) (res int) {
	chMap := map[rune]int{}
	for _, ch := range s {
		chMap[ch]++
	}
	for _, v := range chMap {
		res += v / 2 * 2
	}
	if res < len(s) {
		return res + 1
	}
	return res
}

func main() {
	fmt.Println(longestPalindrome(""))
}
